import{_ as a,Z as e,$ as t,a5 as i}from"./framework-1fb77053.js";const s={},l=i(`<h2 id="_1-基本命令" tabindex="-1"><a class="header-anchor" href="#_1-基本命令" aria-hidden="true">#</a> 1. 基本命令</h2><table><thead><tr><th>命令</th><th style="text-align:left;">描述</th></tr></thead><tbody><tr><td>man</td><td style="text-align:left;">帮助手册</td></tr><tr><td>--help</td><td style="text-align:left;">帮助手册</td></tr><tr><td>date</td><td style="text-align:left;">日期</td></tr><tr><td>cal</td><td style="text-align:left;">日历</td></tr><tr><td>pwd</td><td style="text-align:left;">显示当前所在的目录</td></tr><tr><td>cd</td><td style="text-align:left;">切换目录</td></tr><tr><td>ls</td><td style="text-align:left;">显示当前目录下的内容</td></tr><tr><td>grep</td><td style="text-align:left;">通过 | 管道符，配置 grep 进行过滤筛选</td></tr><tr><td>mkdir</td><td style="text-align:left;">创建目录<br>-p：同时创建多级目录</td></tr><tr><td>touch</td><td style="text-align:left;">创建文件</td></tr><tr><td>rmdir</td><td style="text-align:left;">删除一个空目录</td></tr><tr><td>rm</td><td style="text-align:left;">删除文件或者目录<br>-rvf：递归删除所有目录内容，有提示<br>-rf：递归删除所有目录内容,无提示【慎用】</td></tr><tr><td>cp</td><td style="text-align:left;">复制<br>-r：递归复制整个目录<br>-v：显示复制过程中文件的列表<br>\\cp：强制覆盖不提示</td></tr><tr><td>cat</td><td style="text-align:left;">查看文件<br>cat 文件名 查看轻量级的文本文件<br>cat 文件1 文件2 连接显示多个文件<br>cat 文件1 &gt; 文件2 合并为新文件<br>cat 文件1 &gt;&gt; 文件2 追加</td></tr><tr><td>more</td><td style="text-align:left;">查看比较长的文件<br>空格键：向下翻一页<br>回车键：向下翻一行<br>q：代表立刻离开more<br>ctrl+F 向下滚动一屏<br>ctrl+B 向上滚动一屏</td></tr><tr><td>less</td><td style="text-align:left;">同 more 类似，比 more 功能更多<br>pageDown：向下滚动一页<br>pageUp：向上滚动一页<br>/字符串： 向下搜索指定字符串<br>?字符串：向上搜索指定字符串<br>n：重复前一个搜索<br>N：反向重复前一个搜索</td></tr><tr><td>tail</td><td style="text-align:left;">从尾部开始查看文件，比较适合看日志<br>-f：跟随查看<br>-n&lt;行数&gt; 输出文件尾部 n 行内容</td></tr><tr><td>history</td><td style="text-align:left;">查看命令</td></tr><tr><td>echo</td><td style="text-align:left;">回显，输出一般在 shell 脚本中使用较多</td></tr><tr><td>find</td><td style="text-align:left;">查找文件，提供了丰富的模糊搜索及条件搜索 Find+搜索路径+参数+搜索关键字 按文件名：find /目录/… -name &quot;*.txt&quot;</td></tr><tr><td>locate</td><td style="text-align:left;">查找文件，基于索引，查询速度更快<br>通过 updatedb 来更新索引</td></tr><tr><td>ln</td><td style="text-align:left;">软链接<br>ln -s 原文件或者目录 软链接名</td></tr><tr><td>tar</td><td style="text-align:left;">压缩文件 、解压缩文件<br>tar -zcvf xxx.tar.gz xxxx 压缩文件<br>tar -zxvf xxx.tar.gz 解压缩文件<br>-c：创建一个新归档<br>-x：从归档中解出文件<br>-v：显示详细信息<br>-f：指定压缩后的文件名<br>-z：通过 gzip 过滤归档</td></tr><tr><td>zip</td><td style="text-align:left;">压缩文件<br>zip xxx.zip xxxx<br>zip -r xxx.zip 目录/*</td></tr><tr><td>unzip</td><td style="text-align:left;">解压缩文件 upzip xxx.zip</td></tr></tbody></table><h2 id="_2-分类详细说明" tabindex="-1"><a class="header-anchor" href="#_2-分类详细说明" aria-hidden="true">#</a> 2. 分类详细说明</h2><h3 id="_2-1-帮助命令" tabindex="-1"><a class="header-anchor" href="#_2-1-帮助命令" aria-hidden="true">#</a> 2.1 帮助命令</h3><p><strong>（1）man --- 获得帮助信息</strong></p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">man</span> <span class="token punctuation">[</span>命令或配置文件<span class="token punctuation">]</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>显示说明</p><ul><li><table><thead><tr><th>名称</th><th>描述</th></tr></thead><tbody><tr><td>NAME</td><td>命令的名称和单行描述</td></tr><tr><td>SYNOPSIS</td><td>怎样使用命令</td></tr><tr><td>DESCRIPTION</td><td>命令-功能的深入讨论</td></tr><tr><td>EXAMAPLES</td><td>怎样使用命令的例子</td></tr><tr><td>SEE ALSO</td><td>相关主题（通常是手册页）</td></tr></tbody></table></li></ul></li><li><p>示例</p><ul><li><p>查看 ls 命令的帮助信息</p></li><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">man</span> <span class="token function">ls</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913150804701.png" alt="image-20210913150804701" tabindex="0" loading="lazy"><figcaption>image-20210913150804701</figcaption></li></ul></li></ul></li></ul><p><strong>（2）help --- 获得 shell 内置命令的帮助信息</strong></p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token builtin class-name">help</span> 命令
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>示例</p><ul><li><p>查看 cd 命令的帮助信息</p></li><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token builtin class-name">help</span> <span class="token builtin class-name">cd</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913151049546.png" alt="image-20210913151049546" tabindex="0" loading="lazy"><figcaption>image-20210913151049546</figcaption></li></ul></li></ul></li></ul><p><strong>（3）常用快捷键</strong></p><table><thead><tr><th>命令</th><th>描述</th></tr></thead><tbody><tr><td>ctrl + c</td><td>停止进程</td></tr><tr><td>ctrl + l</td><td>清屏；彻底清屏：reset</td></tr><tr><td>ctrl + q</td><td>退出</td></tr><tr><td>【善于用】tab键</td><td>提示（更重要的是防止敲错）</td></tr><tr><td>上下键</td><td>查找执行过的命令</td></tr><tr><td>ctrl + alt</td><td>linux 和 windows 之间切换</td></tr></tbody></table><h3 id="_2-2-时间日期类" tabindex="-1"><a class="header-anchor" href="#_2-2-时间日期类" aria-hidden="true">#</a> 2.2 时间日期类</h3><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">date</span> <span class="token punctuation">[</span>选项<span class="token punctuation">]</span> <span class="token punctuation">[</span><span class="token punctuation">]</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913151439905.png" alt="image-20210913151439905" tabindex="0" loading="lazy"><figcaption>image-20210913151439905</figcaption></li></ul></li></ul><h3 id="_2-3-文件目录类" tabindex="-1"><a class="header-anchor" href="#_2-3-文件目录类" aria-hidden="true">#</a> 2.3 文件目录类</h3><p><strong>（1）pwd --- 显示当前工作目录的绝对路径</strong></p><blockquote><p>pwd：print working directory 打印工作目录</p></blockquote><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913152327486.png" alt="image-20210913152327486" tabindex="0" loading="lazy"><figcaption>image-20210913152327486</figcaption></figure><p><strong>（2）ls --- 列出目录的内容</strong></p><blockquote><p>ls：list 列表</p></blockquote><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">ls</span> <span class="token punctuation">[</span>选项<span class="token punctuation">]</span> <span class="token punctuation">[</span>目录或是文件<span class="token punctuation">]</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>选项说明</p><ul><li><table><thead><tr><th>选项</th><th>描述</th></tr></thead><tbody><tr><td>-a</td><td>全部的文件，连同隐藏的（开头为 . 的文件）一起列出来【常用】</td></tr><tr><td>-l</td><td>长数据串列出，包含文件的属性和权限等等数据【常用】</td></tr></tbody></table></li></ul></li><li><p>显示说明</p><ul><li>每行列出的信息依次是 <ul><li><strong>文件类型与权限</strong> <strong>链接数</strong> <strong>文件属主</strong> <strong>文件属组</strong> <strong>文件大小(单位byte)</strong> <strong>建立或最近修改的时间</strong> <strong>名称</strong></li></ul></li></ul></li><li><p>示例</p><ul><li><p>查看当前目录的所有内容信息</p></li><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">ls</span> <span class="token parameter variable">-al</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913171356437.png" alt="image-20210913171356437" tabindex="0" loading="lazy"><figcaption>image-20210913171356437</figcaption></li></ul></li></ul></li></ul><p><strong>（3）cd --- 切换目录</strong></p><blockquote><p>cd：change directory 切换目录</p></blockquote><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token builtin class-name">cd</span> <span class="token punctuation">[</span>参数<span class="token punctuation">]</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>参数说明</p><ul><li><table><thead><tr><th>参数</th><th>描述</th></tr></thead><tbody><tr><td>cd 绝对路径</td><td>切换路径</td></tr><tr><td>cd 相对路径</td><td>切换路径</td></tr><tr><td>cd ~ 或者 cd</td><td>回到自己的家目录</td></tr><tr><td>cd -</td><td>回到上一次所在目录</td></tr><tr><td>cd ..</td><td>回到当前目录的上一级目录</td></tr><tr><td>cd -P</td><td>跳转到实际物理路径，而非快捷方式路径</td></tr></tbody></table></li></ul></li><li><p>示例</p><ul><li>使用绝对路劲切换到 root 目录 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913171745666.png" alt="image-20210913171745666" tabindex="0" loading="lazy"><figcaption>image-20210913171745666</figcaption></li></ul></li><li>使用相对路径切换到 opt 目录 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913171800946.png" alt="image-20210913171800946" tabindex="0" loading="lazy"><figcaption>image-20210913171800946</figcaption></li></ul></li><li>回到自己的家目录，即 root 这个目录 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913171817712.png" alt="image-20210913171817712" tabindex="0" loading="lazy"><figcaption>image-20210913171817712</figcaption></li></ul></li><li>回到上一次所在目录 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913171831545.png" alt="image-20210913171831545" tabindex="0" loading="lazy"><figcaption>image-20210913171831545</figcaption></li></ul></li><li>回到当前目录的上一级目录 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913171851064.png" alt="image-20210913171851064" tabindex="0" loading="lazy"><figcaption>image-20210913171851064</figcaption></li></ul></li></ul></li></ul><p><strong>（4）mkdir --- 创建一个新的目录</strong></p><blockquote><p>mkdir：make a directory 建立一个目录</p></blockquote><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mkdir</span> <span class="token punctuation">[</span>选项<span class="token punctuation">]</span> 要创建的目录
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>选项说明：-p 创建多层目录</p></li><li><p>示例</p><ul><li>创建一个目录 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913172046030.png" alt="image-20210913172046030" tabindex="0" loading="lazy"><figcaption>image-20210913172046030</figcaption></li></ul></li><li>创建一个多级目录 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913172056793.png" alt="image-20210913172056793" tabindex="0" loading="lazy"><figcaption>image-20210913172056793</figcaption></li></ul></li></ul></li></ul><p><strong>（5）rmdir --- 删除一个空的目录</strong></p><blockquote><p>rmdir：remove directory 移除目录</p></blockquote><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">rmdir</span> 要删除的目录
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913172300367.png" alt="image-20210913172300367" tabindex="0" loading="lazy"><figcaption>image-20210913172300367</figcaption></li></ul></li></ul><p><strong>（6）touch --- 创建空文件</strong></p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">touch</span> 文件名称
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913172407143.png" alt="image-20210913172407143" tabindex="0" loading="lazy"><figcaption>image-20210913172407143</figcaption></li></ul></li></ul><p><strong>（7）cp --- 复制文件或目录</strong></p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>// 复制 <span class="token builtin class-name">source</span> 文件到 dest
<span class="token function">cp</span> <span class="token punctuation">[</span>选项<span class="token punctuation">]</span> <span class="token builtin class-name">source</span> dest
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div></li></ul></li><li><p>选项说明：-r 递归复制整个文件夹</p></li><li><p>参数说明</p><ul><li><table><thead><tr><th>参数</th><th>描述</th></tr></thead><tbody><tr><td>source</td><td>源文件</td></tr><tr><td>dest</td><td>目标文件</td></tr></tbody></table></li></ul></li><li><p>经验技巧</p><ul><li>强制覆盖不提示的方法：\\cp</li></ul></li><li><p>示例</p><ul><li>复制文件 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913172747987.png" alt="image-20210913172747987" tabindex="0" loading="lazy"><figcaption>image-20210913172747987</figcaption></li></ul></li><li>递归复制整个文件夹 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913172852996.png" alt="image-20210913172852996" tabindex="0" loading="lazy"><figcaption>image-20210913172852996</figcaption></li></ul></li></ul></li></ul><p><strong>（8）rm --- 移除文件或目录</strong></p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">rm</span> <span class="token punctuation">[</span>选项<span class="token punctuation">]</span> deleteFile
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>选项说明</p><ul><li><table><thead><tr><th>选项</th><th>描述</th></tr></thead><tbody><tr><td>-r</td><td>递归删除目录中所有内容</td></tr><tr><td>-f</td><td>强制执行删除操作，不给提示</td></tr><tr><td>-v</td><td>显示指令的详细执行过程</td></tr></tbody></table></li></ul></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913174011539.png" alt="image-20210913174011539" tabindex="0" loading="lazy"><figcaption>image-20210913174011539</figcaption></li></ul></li></ul><p><strong>（9）mv --- 移动文件与目录或重命名</strong></p><ul><li><p>语法</p><ul><li><p>移动文件</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mv</span> /temp/movefile /targetFolder
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>重命名</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mv</span> oldNameFile newNameFile
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li></ul></li><li><p>示例</p><ul><li>移动文件 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913174451660.png" alt="image-20210913174451660" tabindex="0" loading="lazy"><figcaption>image-20210913174451660</figcaption></li></ul></li><li>重命名 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913174459739.png" alt="image-20210913174459739" tabindex="0" loading="lazy"><figcaption>image-20210913174459739</figcaption></li></ul></li></ul></li></ul><p><strong>（10）cat --- 查看文件内容</strong></p><p>查看文件内容，从第一行开始显示</p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">cat</span> <span class="token punctuation">[</span>选项<span class="token punctuation">]</span> 要查看的文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>选项说明：-n 显示所有的行号，包括空行</p></li><li><p>经验技巧</p><ul><li>一般查看比较小的文件，一屏幕能显示全的</li></ul></li><li><p>示例</p><ul><li>查看文件内容并显示行号 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210913174918803.png" alt="image-20210913174918803" tabindex="0" loading="lazy"><figcaption>image-20210913174918803</figcaption></li></ul></li></ul></li></ul><p><strong>（11）more --- 文件内容分屏查看器</strong></p><p>more 指令是一个基于 VI 编辑器的文本过滤器，它以全屏幕的方式按页显示文本文件的内容。more 指令中内置了若干快捷键，详见操作说明。</p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">more</span> 要查看的文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>操作说明</p><ul><li><table><thead><tr><th>操作</th><th>描述</th></tr></thead><tbody><tr><td>空格键(space)</td><td>代表向下翻一页</td></tr><tr><td>Enter</td><td>代表向下翻一行</td></tr><tr><td>q</td><td>代表立即离开more， 不再显示该文件内容</td></tr><tr><td>Ctrl + F</td><td>向下滚动一屏</td></tr><tr><td>Ctrl + B</td><td>返回上一屏</td></tr><tr><td>=</td><td>输出当前的行号</td></tr><tr><td>:f</td><td>输出文件名和当前的行号</td></tr></tbody></table></li></ul></li></ul><p><strong>（12）less --- 分屏显示文件内容</strong></p><p>less 指令用来分屏查看文件内容，它的功能与 more 指令类似,但是比 more 指令更加强大，支持各种显示终端。less 指令在显示文件内容时，并不是一次将整个文件加载之后才显示，而是根据显示需要加载内容，对于显示大型文件具有较高的效率。</p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">less</span> 要查看的文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>操作说明</p><ul><li><table><thead><tr><th>操作</th><th>描述</th></tr></thead><tbody><tr><td>空格键(space)</td><td>向下翻动一页</td></tr><tr><td>【pagedown】</td><td>向下翻动一页</td></tr><tr><td>【pageup】</td><td>向上翻动一页</td></tr><tr><td>/字符串</td><td>向下搜寻【字符串】的功能；n:向下查找，N:向上查找；</td></tr><tr><td>?字符串</td><td>向上搜寻【字符串】的功能；n:向上查找，N:向下查找；</td></tr><tr><td>q</td><td>离开less这个程序</td></tr></tbody></table></li></ul></li></ul><p><strong>（13）echo --- 输出内容到控制台</strong></p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token builtin class-name">echo</span> <span class="token punctuation">[</span>选项<span class="token punctuation">]</span> <span class="token punctuation">[</span>输出内容<span class="token punctuation">]</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>选项说明：-e 支持反斜线控制的字符转换</p><ul><li><table><thead><tr><th>控制字符</th><th>描述</th></tr></thead><tbody><tr><td>\\</td><td>输出\\本身</td></tr><tr><td>\\n</td><td>换行符</td></tr><tr><td>\\t</td><td>制表符，也就是Tab键</td></tr></tbody></table></li></ul></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914090319680.png" alt="image-20210914090319680" tabindex="0" loading="lazy"><figcaption>image-20210914090319680</figcaption></li></ul></li></ul><p><strong>（14）head --- 显示文件头部内容</strong></p><p>head 用于显示文件的开头部分内容，默认情况下 head 指令显示文件的前 10 行内容。</p><ul><li><p>语法</p><ul><li><p>查看文件头 10 行内容</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">head</span> 文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>查看文件头 5 行内容，5 可以是任意行数</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">head</span> <span class="token parameter variable">-n5</span> 文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li></ul></li><li><p>选项说明：-n&lt;行数&gt; 指定显示头部内容的行数</p></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914090757206.png" alt="image-20210914090757206" tabindex="0" loading="lazy"><figcaption>image-20210914090757206</figcaption></li></ul></li></ul><p><strong>（15）tail --- 输出文件尾部内容</strong></p><p>tail 用于输出文件中尾部的内容，默认情况下 tail 指令显示文件的最后 10 行内容。</p><ul><li><p>语法</p><ul><li><p>查看文件的最后 10 行内容</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">tail</span> 文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>查看文件最后 5 行内容，5 可以是任意行数</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">tail</span> <span class="token parameter variable">-n5</span> 文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>实时追踪该文件的所有更新</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">tail</span> <span class="token parameter variable">-f</span> 文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li></ul></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914091032238.png" alt="image-20210914091032238" tabindex="0" loading="lazy"><figcaption>image-20210914091032238</figcaption></li></ul></li></ul><p><strong>（16）&gt; 输出重定向和 &gt;&gt; 追加</strong></p><ul><li><p>语法</p><ul><li><p>列表的内容写入到文件中（覆盖写）</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">ls</span> <span class="token parameter variable">-l</span> <span class="token operator">&gt;</span> 文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>列表的内容追加到文件的末尾</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">ls</span> <span class="token parameter variable">-al</span> <span class="token operator">&gt;&gt;</span> 文件
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>将 文件1 的内容覆盖到 文件2</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">cat</span> 文件1 <span class="token operator">&gt;</span> 文件2
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>使用 echo 将内容追加到文件末尾</p><ul><li>echo &quot;内容&quot; &gt;&gt; 文件</li></ul></li></ul></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914091436465.png" alt="image-20210914091436465" tabindex="0" loading="lazy"><figcaption>image-20210914091436465</figcaption></li></ul></li></ul><p><strong>（17）ln --- 软链接</strong></p><p>软链接也称为符号链接，类似于 Windows 里的快捷方式，有自己的数据块，主要存放了链接其他文件的路径。</p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">ln</span> <span class="token parameter variable">-s</span> <span class="token punctuation">[</span>原文件或目录<span class="token punctuation">]</span> <span class="token punctuation">[</span>软链接名<span class="token punctuation">]</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>经验技巧</p><ul><li>删除软链接：rm -rf 软链接名，而不是 rm -rf 软链接名/</li><li>查询：通过 ll 就可以查看，列表属性第1位是1，尾部会有位置指向</li></ul></li><li><p>示例</p><ul><li>创建软链接 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914091820381.png" alt="image-20210914091820381" tabindex="0" loading="lazy"><figcaption>image-20210914091820381</figcaption></li></ul></li><li>删除软链接 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914091840891.png" alt="image-20210914091840891" tabindex="0" loading="lazy"><figcaption>image-20210914091840891</figcaption></li></ul></li><li>进入软链接实际物理路径 <ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914091900603.png" alt="image-20210914091900603" tabindex="0" loading="lazy"><figcaption>image-20210914091900603</figcaption></li></ul></li></ul></li></ul><p><strong>（18）history --- 查看已经执行过的历史命令</strong></p><ul><li><p>语法</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">history</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li><li><p>示例</p><ul><li><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914091940740.png" alt="image-20210914091940740" tabindex="0" loading="lazy"><figcaption>image-20210914091940740</figcaption></li></ul></li></ul><h2 id="_3-磁盘分区类命令" tabindex="-1"><a class="header-anchor" href="#_3-磁盘分区类命令" aria-hidden="true">#</a> 3. 磁盘分区类命令</h2><h3 id="_3-1-windows-下的磁盘分区" tabindex="-1"><a class="header-anchor" href="#_3-1-windows-下的磁盘分区" aria-hidden="true">#</a> 3.1 Windows 下的磁盘分区</h3><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914092051619.png" alt="image-20210914092051619" tabindex="0" loading="lazy"><figcaption>image-20210914092051619</figcaption></figure><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914092127706.png" alt="image-20210914092127706" tabindex="0" loading="lazy"><figcaption>image-20210914092127706</figcaption></figure><h3 id="_3-2-常用的两种磁盘分区类型" tabindex="-1"><a class="header-anchor" href="#_3-2-常用的两种磁盘分区类型" aria-hidden="true">#</a> 3.2 常用的两种磁盘分区类型</h3><p>（1）<strong>mbr</strong></p><p>①操作系统要安装在主分区</p><p>②只支持 4 个主分区</p><p>③扩展分区占一个主分区</p><p>（2）<strong>gpt</strong>(win7 64位以后)</p><p>①无限主分区</p><p>②支持超大硬盘 3T 以上</p><p>（3）Linux 下查看所有设备的挂载情况</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>lsblk 或者 lsblk <span class="token parameter variable">-f</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>（4）增加一块新硬盘，通过命令进行分区</p><p><strong>①虚拟机插入新硬盘</strong></p><p>在【虚拟机】菜单中，选择【设置】，然后设备列表里添加硬盘，然后一路【下一步】，中间只有选择磁盘大小的地方需要修改，直至完成，然后重启系统</p><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914092349284.png" alt="image-20210914092349284" tabindex="0" loading="lazy"><figcaption>image-20210914092349284</figcaption></figure><p><strong>②进行分区</strong></p><p>通过 fdisk/dev/sdb 开始对 /sdb 进行分区</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>m 显示命令列表

p 显示磁盘

n 新增分区

d 删除分区

w 入并退出分区
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>分区步骤如下：开始分区后输入 n，新增分区，然后选择 p，分区类型为主分区。两次回车默认剩余全部空间，最后输入 w 写入分区并退出，若不保存退出输入 q。</p><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914092514099.png" alt="image-20210914092514099" tabindex="0" loading="lazy"><figcaption>image-20210914092514099</figcaption></figure><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914092538068.png" alt="image-20210914092538068" tabindex="0" loading="lazy"><figcaption>image-20210914092538068</figcaption></figure><p><strong>③格式化磁盘</strong></p><p>命令：</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mkfs</span> <span class="token parameter variable">-t</span> ext4 /dev/sdb1
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><strong>④挂载</strong></p><p>将一个分区与一个目录联系起来</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">mount</span> 设备名称 挂载目录
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>例如：mount /dev/sdb1 /netdisk</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">umount</span> 设备名称 或者 挂载目录
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>例如：umount /dev/sdb1 或者 umount /netdisk</p><p>注意：用命令挂载重启后会失效</p><p><strong>⑤永久挂载</strong></p><p>通过修改 /etc/fstab 实现挂载</p><p>添加完成后 执行 mount -a 即刻生效</p><p>/etc/fatab 文件参数介绍</p><p>第一列：磁盘设备文件或者该设备的 Label 或者 UUID</p><p>第二列: 设备的挂载点，就是你要挂载到哪个目录下</p><p>第三列: 磁盘文件系统的格式，包括 ext2、ext3、reiserfs、nfs、vfat 等</p><p>第四列: 文件系统的参数 ，defaults 代表同时具有 rw,suid,dev,exec,auto,nouser,async 等默认参数的设置</p><p>第五列: 能否被 dump 备份命令作用</p><p>     0 代表不要做 dump 备份</p><p>     1 代表要每天进行 dump 的操作</p><p>     2 代表不定日期的进行 dump 操作</p><p>第六列: 是否检验扇区</p><p>     0 不要检验</p><p>     1 最早检验（一般根目录会选择）</p><p>     2 1级别检验完成之后进行检验</p><h2 id="_4-磁盘情况查询" tabindex="-1"><a class="header-anchor" href="#_4-磁盘情况查询" aria-hidden="true">#</a> 4. 磁盘情况查询</h2><ul><li><p>df -h：查询系统整体磁盘使用情况</p></li><li><p>du -h /目录：查询指定目录的磁盘占用情况,默认为当前目录</p></li><li><p>参数说明：</p><ul><li>-s 指定目录占用大小汇总</li><li>-h 带计量单位</li><li>-a 含文件</li><li>--max-depth=1 子目录深度</li><li>-c 列出明细的同时，增加汇总值</li></ul></li><li><p>使用举例</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">du</span> <span class="token parameter variable">-ach</span> --max-depth<span class="token operator">=</span><span class="token number">1</span> /opt
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul></li></ul><h2 id="_5-网络配置类" tabindex="-1"><a class="header-anchor" href="#_5-网络配置类" aria-hidden="true">#</a> 5. 网络配置类</h2><h3 id="_5-1-ifconfig-查看网络配置" tabindex="-1"><a class="header-anchor" href="#_5-1-ifconfig-查看网络配置" aria-hidden="true">#</a> 5.1 ifconfig 查看网络配置</h3><p>5.2 如何修改 IP</p><p>（1）图形化操作</p><p>（2）命令行方式</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">vi</span> /etc/sysconfig/network-scripts/ifcfg-ens33
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>或者</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">vi</span> /etc/sysconfig/network-scripts/ifcfg-eth0
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><strong>参数说明：</strong></p><div class="language-text line-numbers-mode" data-ext="text"><pre class="language-text"><code>DEVICE=eth0 # 接口名（设备,网卡）
BOOTPROTO=none # IP的配置方法[none|static|bootp|dhcp]（引导时不使用协议|静态分配IP|BOOTP协议|DHCP 协议）
BROADCAST=192.168.1.255 # 广播地址    
HWADDR=00:0C:2x:6x:0x:xx # MAC地址
IPADDR=192.168.1.23 # IP地址
NETMASK=255.255.255.0 # 网络掩码  
NETWORK=192.168.1.0 # 网络地址
ONBOOT=yes # 系统启动的时候网络接口是否有效（yes/no）
TYPE=Ethernet # 网络类型（通常是Ethernet）
GATEWAY=192.168.1.2 # 网关
DNS1=192.168.1.2 # 域名解析器
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h2 id="_6-进程类" tabindex="-1"><a class="header-anchor" href="#_6-进程类" aria-hidden="true">#</a> 6. 进程类</h2><h3 id="_6-1-ps-process" tabindex="-1"><a class="header-anchor" href="#_6-1-ps-process" aria-hidden="true">#</a> 6.1 ps（process）</h3><p>ps 显示的信息选项：</p><table><thead><tr><th>字段</th><th>说明</th></tr></thead><tbody><tr><td>PID</td><td>进程识别号</td></tr><tr><td>TTY</td><td>终端机号</td></tr><tr><td>TIME</td><td>此进程所消 CPU 时间</td></tr><tr><td>CMD</td><td>正在执行的命令或进程名</td></tr></tbody></table><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914095310780.png" alt="image-20210914095310780" tabindex="0" loading="lazy"><figcaption>image-20210914095310780</figcaption></figure><h3 id="_6-2-ps-aux" tabindex="-1"><a class="header-anchor" href="#_6-2-ps-aux" aria-hidden="true">#</a> 6.2 ps -aux</h3><p>-a：显示当前总段的所有进程信息（选择所有进程）</p><p>-u：以用户的格式显示进程信息</p><p>-x：显示后台进程（没有终端）</p><p><strong>详细解释：</strong></p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">ps</span> <span class="token parameter variable">-aux</span> <span class="token operator">|</span> <span class="token function">grep</span> xxx
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914095801366.png" alt="image-20210914095801366" tabindex="0" loading="lazy"><figcaption>image-20210914095801366</figcaption></figure><ul><li>USER：用户名称</li><li>PID：进程号</li><li>%CPU：进程占用 CPU 资源的百分比，占用越高，进程越耗费资源</li><li>%MEM：进程占用物理内存的百分比，占用越高，进程越耗费资源</li><li>VSZ：进程占用的虚拟内存大小(单位：KB)</li><li>RSS：进程占用的物理内存大小(单位：KB)</li><li>TTY：终端名称,缩写（其中 tty1-tty7 代表本地控制台终端，tty1-tty6 是本地的字符界面终端，tty7 是图形终端。pts/0-255 代表虚拟终端）</li><li>STAT：进程状态，其中S-睡眠，s-表示该进程是会话的先导进程，N-表示进程拥有比普通优先级更低的优先级，R-正在运行，D-短期等待，Z-僵死进程，T-被跟踪或者被停止等等</li><li>(R：运行、S：睡眠、T：停止状态、s：包含子进程、+：位于后台)</li><li>START：进程的启动时间</li><li>TIME：该进程占用 CPU 的运算时间，注意不是系统时间</li><li>COMMAND：启动进程所用的命令和参数，如果过长会被截断显示</li></ul><h3 id="_6-3-ps-ef" tabindex="-1"><a class="header-anchor" href="#_6-3-ps-ef" aria-hidden="true">#</a> 6.3 ps -ef</h3><blockquote><p><strong>ps -ef：是以全格式显示当前所有的进程</strong></p></blockquote><p>-e 显示所有进程</p><p>-f 全格式</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">ps</span> <span class="token parameter variable">-ef</span> <span class="token operator">|</span> <span class="token function">grep</span> xxx
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914100133238.png" alt="image-20210914100133238" tabindex="0" loading="lazy"><figcaption>image-20210914100133238</figcaption></figure><ul><li>UID：用户 ID</li><li>PID：进程 ID</li><li>PPID：父进程 ID</li><li>C：CPU 用于计算执行优先级的因子。数值越大，表明进程是 CPU 密集型运算，执行优先级会降低；数值越小，表明进程是 I/O 密集型运算，执行优先级会提高</li><li>STIME：进程启动的时间</li><li>TTY：完整的终端名称</li><li>TIME：CPU 时间</li><li>CMD：启动进程所用的命令和参数</li></ul><h3 id="_6-4-kill-pid" tabindex="-1"><a class="header-anchor" href="#_6-4-kill-pid" aria-hidden="true">#</a> 6.4 kill pid</h3><p>杀死指定的 pid 对应的进程（-9：强行杀死进程）</p><h2 id="_7-服务类" tabindex="-1"><a class="header-anchor" href="#_7-服务类" aria-hidden="true">#</a> 7. 服务类</h2><h3 id="_7-1-service-centos6" tabindex="-1"><a class="header-anchor" href="#_7-1-service-centos6" aria-hidden="true">#</a> 7.1 service（CentOS6）</h3><p>（1）注册在系统中的标准化程序</p><p>（2）有方便统一的管理方式（常用的方法）</p><ul><li>service 服务名 start &gt;&gt;&gt; 开启服务</li><li>service 服务名 stop &gt;&gt;&gt; 关闭服务</li><li>service 服务名 restart &gt;&gt;&gt; 重新启动服务</li><li>service 服务名 reload &gt;&gt;&gt; 重新加载服务</li><li>service 服务名 status &gt;&gt;&gt; 查看服务状态</li></ul><p>（3）查看服务的方法 /etc/init.d/ 服务名</p><p>（4）通过 chkconfig 命令设置自启动</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>// 查看服务开机启动状态
<span class="token function">chkconfig</span> 服务名 <span class="token parameter variable">--list</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div></li><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>// 开启指定服务的自动启动
<span class="token function">chkconfig</span> <span class="token punctuation">(</span>--level <span class="token number">5</span><span class="token punctuation">)</span> 服务名 on
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div></li><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>// 关闭指定服务的自动启动
<span class="token function">chkconfig</span> <span class="token punctuation">(</span>--level <span class="token number">5</span><span class="token punctuation">)</span> 服务名 off
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div></li></ul><p>（5）运行级别</p><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914100800943.png" alt="image-20210914100800943" tabindex="0" loading="lazy"><figcaption>image-20210914100800943</figcaption></figure><p>查看默认级别：vi /etc/inittab</p><ul><li>Linux 系统有 7 种运行级别(runlevel)：常用的是级别 3 和 5</li><li>运行级别 0：系统停机状态，系统默认运行级别不能设为 0，否则不能正常启动</li><li>运行级别 1：单用户工作状态，root 权限，用于系统维护，禁止远程登陆</li><li>运行级别 2：多用户状态(没有 NFS)，不支持网络</li><li>运行级别 3：完全的多用户状态(有 NFS)，登陆后进入控制台命令行模式</li><li>运行级别 4：系统未使用，保留</li><li>运行级别 5：X11 控制台，登陆后进入图形 GUI 模式</li><li>运行级别 6：系统正常关闭并重启，默认运行级别不能设为 6，否则不能正常启动</li></ul><h3 id="_7-2-systemctl-centos7" tabindex="-1"><a class="header-anchor" href="#_7-2-systemctl-centos7" aria-hidden="true">#</a> 7.2 systemctl（CentOS7）</h3><p>（1）注册在系统中的标准化程序</p><p>（2）有方便统一的管理方式（常用的方法）</p><ul><li>systemctl 服务名 start &gt;&gt;&gt; 开启服务</li><li>systemctl 服务名 stop &gt;&gt;&gt; 关闭服务</li><li>systemctl 服务名 restart &gt;&gt;&gt; 重新启动服务</li><li>systemctl 服务名 reload &gt;&gt;&gt; 重新加载服务</li><li>systemctl 服务名 status &gt;&gt;&gt; 查看服务状态</li></ul><p>（3）查看服务的方法 /usr/lib/systemd/system</p><p>（4）查看服务的命令</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>systemctl list-unit-files
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>systemctl <span class="token parameter variable">--type</span> <span class="token function">service</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div></li></ul><p>（5）通过 systemctl 命令设置自启动</p><ul><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>// 自启动
systemctl <span class="token builtin class-name">enable</span> service_name
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div></li><li><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code>// 不自启动
systemctl disable service_name
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div></li></ul><p>（6）运行级别</p><figure><img src="https://pet-hkw.oss-cn-shenzhen.aliyuncs.com/image/new_blog_system/linux/image-20210914100800943.png" alt="image-20210914100800943" tabindex="0" loading="lazy"><figcaption>image-20210914100800943</figcaption></figure><p>查看默认级别：vi /etc/inittab</p><p>CentOS7 运行级别简化为：</p><table><thead><tr><th>init 级别</th><th>systemctl target</th></tr></thead><tbody><tr><td>0</td><td>shutdown.target</td></tr><tr><td>1</td><td>emergency.target</td></tr><tr><td>2</td><td>rescure.target</td></tr><tr><td>3</td><td>multi-user.target</td></tr><tr><td>4</td><td>无</td></tr><tr><td>5</td><td>graphical.target</td></tr><tr><td>6</td><td>无</td></tr></tbody></table><p>get-default：取得当前的 target</p><p>set-default：设置指定的 target 为默认的运行级别</p><p>isolate：切换到指定的运行级别</p><table><thead><tr><th>systemctl 命令</th><th>说明</th></tr></thead><tbody><tr><td>systemctl get-default</td><td>获得当前的运行级别</td></tr><tr><td>systemctl set-default multi-user.target</td><td>设置默认的运行级别为 mulit-user</td></tr><tr><td>systemctl isolate multi-user.target</td><td>在不重启的情况下，切换到运行级别 mulit-user 下</td></tr><tr><td>systemctl isolate graphical.target</td><td>在不重启的情况下，切换到图形界面下</td></tr></tbody></table><h2 id="_8-netstat" tabindex="-1"><a class="header-anchor" href="#_8-netstat" aria-hidden="true">#</a> 8. netstat</h2><blockquote><p><strong>查看系统的网络情况</strong></p></blockquote><p>-an 按一定的顺序排列输出</p><p>-p 显示哪个进程在调用</p><div class="language-bash line-numbers-mode" data-ext="sh"><pre class="language-bash"><code><span class="token function">netstat</span> <span class="token parameter variable">-anp</span> <span class="token operator">|</span> <span class="token function">grep</span> <span class="token number">8080</span> // 查看占用 <span class="token number">8080</span> 端口的进程
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div>`,179),n=[l];function d(r,p){return e(),t("div",null,n)}const u=a(s,[["render",d],["__file","linux-basic-5.html.vue"]]);export{u as default};
